const ReviewCard = ({ data }) => {
  return (
    <ul>
      {data.reviews.map((review) => {
        return (
          <li key={review.review_id}>
            <h3>{review.title}</h3>
            <p>{review.review_body}</p>
            <img src={review.eview_img_url} alt={review.title} />
            <p>{review.created_at}</p>
          </li>
        );
      })}
    </ul>
  );
};

export default ReviewCard;
