import React, { useState } from "react";
import API from "../axios/API";
import IndvReview from "./IndvReview";

const ReviewCard = ({ data }) => {
  const [clickedReview, setclickedReview] = useState(null);

  const handleClick = (review_id) => {
    API.get(`/api/reviews/${review_id}`)
      .then((response) => {
        setclickedReview(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <ul className="review-card">
        {data.reviews.map((review) => (
          <li
            key={review.review_id}
            onClick={() => handleClick(review.review_id)}
          >
            <h3>{review.title}</h3>
            <h4>Designed by {review.designer}</h4>
            <p>owner: {review.owner}</p>
            <img src={review.review_img_url} alt={review.title} />
            <p>Category: {review.category}</p>
            <p>Created at {new Date(review.created_at).toLocaleString("en")}</p>
          </li>
        ))}
      </ul>
      {clickedReview ? <IndvReview review={clickedReview} /> : null}
    </div>
  );
};

export default ReviewCard;
